"use strict";jQuery(document).ready(function(){$(".collapse").click(function(){var a=$(this).parents(".address");var b=a.children(".sub-address");b.toggle();});$(window).scroll(function(){if($(this).scrollTop()>500){$("#go-top").fadeIn();}else{$("#go-top").fadeOut();}});$("#go-top").click(function(){$("html, body").animate({scrollTop:0},600);return false;});var c=function(d){d.preventDefault();var e=$('#deliver-now').is(':checked'),withDelivery=$('#with-delivery').is(':checked');if(!e&&!withDelivery){window.location.href='/';return;}if(e){window.location.href='/deliver-now?sort=deliveryPrice';return;}if(withDelivery){window.location.href='/with-delivery?sort=deliveryPrice';return;}};$('#deliver-now').click(function(d){var f=$(this).is(':checked');if(!f){$("#deliver-now").prop('checked',false);}c(d);});$('#with-delivery').click(c);$('.sorting').click(function(d){d.preventDefault();var g=window.location.href;g=g.substr(0,g.indexOf('?'));window.location.href=g+'?sort='+$(this).data('sort');});});
